public class Main &#123;
    public static void main(String[] args) &#123;
        int[] nums = new int[]&#123;235, 233, 110, 789, 5, 0, 1&#125;;
        for (int item : nums) &#123;
            new Thread(() -&gt; &#123;
                try &#123;
                    Thread.sleep(item);
                &#125; catch (InterruptedException e) &#123;
                    e.printStackTrace();
                &#125;
                System.out.println(item);
            &#125;).start();
        &#125;
